CREATE DATABASE ProjetoUSE Projeto;CREATE TABLE empregado(codEmpregado INT NOT NULL PRIMARY KEY,nomeInic VARCHAR(20) NOT NULL,nomeInt CHAR(1) NULL,nomeFin VARCHAR(20) NULL,dataNasc DATETIME NULL,endereco VARCHAR(60) NULL,sexo CHAR(9)NUll,salario MONEY NULL)CREATE TABLE dependente(codDependente INT NOT NULL PRIMARY KEY,nomeDependente VARCHAR(60)NOT NULL,sexoDep CHAR(1)NOT NULL,dataNascDep DATETIME,tipoDep CHAR(1)NULL,codEmpregado INT NOT NULL FOREIGN KEY REFERENCES empregado (codEmpregado))CREATE TABLE cidade(codCidade INT NOT NULL PRIMARY KEY,nomeCidade VARCHAR(60)NOT NULL)CREATE TABLE departamento(CodDepartamento INT NOT NULL PRIMARY KEY,nomeDepartamento VARCHAR(60)NOT NULL,dataInicGer DATE NOT NULL,codCidade INT NOT NULL FOREIGN KEY REFERENCES cidade (codCidade))CREATE TABLE projeto(codProjeto INT NOT NULL PRIMARY KEY,nomeProjeto VARCHAR (60) NOT NULL,codDepartamento INT NOT NULL FOREIGN KEY REFERENCES departamento(codDepartamento))CREATE TABLE empregadoProjeto(horas INT NOT NULL,codEmpregado INT NULL FOREIGN KEY REFERENCES empregado (codEmpregado),codProjeto INT NOT NULL FOREIGN KEYREFERENCES projeto (codProjeto))-- 1 INSERT cidade (codCidade,nomeCidade)  VALUES (1,'campo grande'), (2,'Dourados'), (3,'corumb� '), (4,'Tr�s Lagoas'), (5,'Ponta Por�'), (6,'Rio Verde'), (7,'Porto Murtinho'), (8,'Bonito'), (9,'Paranaiba')-- 2 UPDATE cidade SET nomeCidade = ('Paranaiba') WHERE nomeCidade = ('cassil�ncia') -- 3  INSERT departamento VALUES 	 (10,'Ricardin','01-05-2006 14:58:00',1),	 (11,'Rbc Eletricas','16-05-2008 16:00:00',2),	 (12,'ABC','31-01-2005 14:58:00',3),	 (13,'CAD AUTO','23-06-2007 18:20:00',4) INSERT projeto VALUES	(14,'A',10),	(15,'B',11),	(16,'C',12),	(17,'D',13)INSERT empregadoVALUES	(18,'Nicholas','A','SILVA','01-05-2006 19:30:00','Rua Saturnino Pereira', 'M', $1000.00),	(19,'Pedro','H','Raimundo','25-01-2006 16:30:00','Rua de Aruja','M', $3000.00),	(20,'Henrique','B','de